mapper.select:
SELECT R.dclr_id, R.dclr_trgt_type_cd, R.prcs_stts_cd, R.dclr_rcpt_dt, U.user_ncnm AS dclrUserNickname, U.user_lgn_id AS dclrUserLoginId, RR.dclr_rsn_cn AS dclrReasonContent, COALESCE(R.dclr_trgt_user_no, R.dclr_trgt_conts_id) AS intelligentTargetInfo FROM reports AS R JOIN users AS U ON R.dclr_user_no = U.user_no JOIN report_reasons AS RR ON R.dclr_rsn_cd = RR.dclr_rsn_cd
AND R.prcs_stts_cd = #{searchCriteria.prcsSttsCd}
AND R.dclr_rcpt_dt >= #{searchCriteria.startDate}
AND R.dclr_rcpt_dt <= DATE_ADD(#{searchCriteria.endDate}, INTERVAL 1 DAY)
AND R.dclr_id LIKE CONCAT('%', #{searchCriteria.searchValue}, '%')
AND R.dclr_user_no IN (SELECT user_no FROM users WHERE user_lgn_id LIKE CONCAT('%', #{searchCriteria.searchValue}, '%'))
AND R.dclr_trgt_type_cd LIKE CONCAT('%', #{searchCriteria.searchValue}, '%')
AND RR.dclr_rsn_cn LIKE CONCAT('%', #{searchCriteria.searchValue}, '%')
ORDER BY R.dclr_rcpt_dt DESC LIMIT #{pagination.recordSize} OFFSET #{pagination.limitStart}

mapper.update:
UPDATE users
SET
user_status = #{status}
WHERE
user_no =
#{userNo};
