mapper.select:
SELECT COUNT(ss.store_clcln_id) FROM store_settlements ss LEFT JOIN stores s ON ss.store_id = s.store_id LEFT JOIN cdfp c ON ss.plcy_id = c.plcy_id
AND ss.store_id LIKE CONCAT('%', #{searchValue}, '%')
AND ss.plcy_id LIKE CONCAT('%', #{searchValue}, '%')
AND ss.store_clcln_id LIKE CONCAT('%', #{searchValue}, '%')
AND ss.clcln_stts_cd = #{searchValue}
AND s.store_conm LIKE CONCAT('%', #{searchValue}, '%')
AND c.plcy_expln LIKE CONCAT('%', #{searchValue}, '%')
AND ss.clcln_prcs_ymd BETWEEN #{startDate} AND #{endDate}
AND ss.clcln_stts_cd IN
#{item}

SELECT ss.store_clcln_id, ss.store_id, s.store_conm, ss.plcy_id, c.plcy_expln, ss.tot_sel_amt, ss.sel_fee_rt, ss.clcln_amt, ss.actno_id, ss.clcln_prcs_ymd, ss.clcln_stts_cd FROM store_settlements ss LEFT JOIN stores s ON ss.store_id = s.store_id LEFT JOIN cdfp c ON ss.plcy_id = c.plcy_id
AND ss.store_id LIKE CONCAT('%', #{searchValue}, '%')
AND ss.plcy_id LIKE CONCAT('%', #{searchValue}, '%')
AND ss.clcln_stts_cd = #{searchValue}
AND s.store_conm LIKE CONCAT('%', #{searchValue}, '%')
AND c.plcy_expln LIKE CONCAT('%', #{searchValue}, '%')
AND ss.clcln_prcs_ymd BETWEEN #{startDate} AND #{endDate}
AND ss.clcln_stts_cd IN
#{item}
ORDER BY -- ⭐ 이 부분이 핵심입니다. 정산 상태에 따라 우선순위를 부여합니다. CASE ss.clcln_stts_cd WHEN '판매정산대기' THEN 1 WHEN '판매정산완료' THEN 2 ELSE 3 -- 그 외 다른 상태가 있다면 이 순서로 END, ss.clcln_prcs_ymd DESC, -- 그 다음 정산 처리일 기준 내림차순 ss.store_clcln_id DESC -- 마지막으로 정산 ID 기준 내림차순 LIMIT #{offset}, #{pageSize}

mapper.insert:
    
        SELECT
            CONCAT(
                'stl_sale_',
                /* 100 미만이면 0을 채워 3자리로, 100 이상이면 그대로 표시 */
                LPAD(
                    /* 테이블에서 가장 큰 숫자 + 1 을 계산 */
                    IFNULL(MAX(CAST(SUBSTRING_INDEX(store_clcln_id, '_', -1) AS UNSIGNED)), 0) + 1,
                    3,
                    '0'
                )
            ) AS next_id
        FROM
            store_settlements
        WHERE
            store_clcln_id LIKE 'stl_sale_%'
    

    INSERT INTO store_settlements (
        store_clcln_id, store_id, plcy_id, tot_sel_amt, sel_fee_rt,
        clcln_amt, actno_id, clcln_prcs_ymd, clcln_stts_cd
    ) VALUES (
        #{storeClclnId}, #{storeId}, #{plcyId}, #{totSelAmt}, #{selFeeRt},
        #{clclnAmt}, #{actnoId}, #{clclnPrcsYmd}, #{clclnSttsCd}
    )

mapper.update:
UPDATE store_settlements
   SET store_clcln_id = #{storeClclnId}
 WHERE id = #{id}
