mapper.select:
/* 사용자가 보유한 쿠폰 목록 조회 */ SELECT uc.user_cpn_id, uc.user_no, uc.pblcn_cpn_id, uc.use_yn, uc.cpn_give_dt, c.vld_end_dt, c.cpn_nm, c.dscnt_vl, c.min_ordr_amt, c.max_dscnt_amt FROM user_coupons uc JOIN coupons c ON uc.pblcn_cpn_id = c.pblcn_cpn_id WHERE uc.user_no = #{userNo}
AND c.cpn_nm LIKE CONCAT('%', #{keyword}, '%')
ORDER BY
c.vld_end_dt ASC c.dscnt_vl DESC uc.cpn_give_dt DESC c.vld_end_dt ASC
LIMIT #{limit} OFFSET #{offset}

/* 단일 쿠폰의 상세 정보 조회 */
SELECT * FROM coupons WHERE
pblcn_cpn_id = #{couponId}

/* 사용자의 특정 쿠폰
보유 여부 확인 */
SELECT COUNT(*) FROM user_coupons WHERE user_no = #{userNo}
AND pblcn_cpn_id
= #{couponId}

SELECT COUNT(*) 
FROM orders 
WHERE user_no = #{userNo}

SELECT COUNT(*) 
FROM reviews 
WHERE writer_user_no = #{userNo}

SELECT COUNT(*) 
FROM user_coupons 
WHERE user_no = #{userNo}
  AND pblcn_cpn_id = #{couponId}
  AND issu_rsn_src_cn = 'REVIEW'
  AND DATE_FORMAT(cpn_give_dt, '%Y-%m') = DATE_FORMAT(NOW(), '%Y-%m')

SELECT COUNT(*) 
FROM user_coupons 
WHERE user_no = #{userNo}
  AND pblcn_cpn_id = #{couponId}
  AND issu_rsn_src_cn = 'BIRTHDAY'
  AND YEAR(cpn_give_dt) = YEAR(NOW())

SELECT COUNT(*) 
FROM user_coupons 
WHERE user_no = #{userNo}
  AND pblcn_cpn_id = #{couponId}
  AND DATE_FORMAT(cpn_give_dt, '%Y-%m') = DATE_FORMAT(NOW(), '%Y-%m')

SELECT COUNT(*) 
FROM user_coupons
WHERE user_no = #{userNo}
  AND pblcn_cpn_id = #{couponId}
  AND YEARWEEK(cpn_give_dt, 1) = YEARWEEK(NOW(), 1)

SELECT COUNT(*) 
FROM user_coupons
WHERE user_no = #{userNo}
  AND pblcn_cpn_id = #{couponId}
  AND YEAR(cpn_give_dt) = YEAR(NOW())
