mapper.select:
SELECT f.feed_sn, f.wrtr_user_no, f.feed_cn, f.crt_dt, u.user_no, u.user_ncnm, up.prfl_img, (SELECT COUNT(*) FROM feed_interactions fi2 WHERE fi2.feed_sn = f.feed_sn AND fi2.intract_type_cd = 'LIKE' AND fi2.rtrcn_dt IS NULL) AS like_count FROM ( SELECT * FROM feeds WHERE crt_dt < STR_TO_DATE(#{currentFeedCrtDt}, '%Y-%m-%d %H:%i:%s') AND del_dt IS NULL
AND wrtr_user_no = #{userNo}
ORDER BY crt_dt DESC LIMIT #{limit} ) f LEFT JOIN users u ON f.wrtr_user_no = u.user_no LEFT JOIN user_profiles up ON u.user_no = up.user_no ORDER BY f.crt_dt DESC

SELECT f.feed_sn, f.wrtr_user_no, f.feed_cn, f.crt_dt, u.user_no, u.user_ncnm, fi.img_file_path_nm, ( SELECT COUNT(fi2.feed_intract_sn) FROM feed_interactions fi2 WHERE fi2.feed_sn = f.feed_sn AND fi2.intract_type_cd = 'like' AND fi2.rtrcn_dt IS NULL ) AS like_count FROM feeds f LEFT JOIN users u ON f.wrtr_user_no = u.user_no LEFT JOIN feed_images fi ON f.feed_sn = fi.feed_sn AND fi.feed_img_sort_sn = 0 WHERE f.del_dt IS NULL
AND f.wrtr_user_no = #{userNo}
ORDER BY f.crt_dt DESC LIMIT #{limit} OFFSET #{offset}

SELECT ft.feed_tag_sn, ft.feed_sn, ft.tag_sn, ft.crt_dt, t.tag_nm
FROM feed_tags ft JOIN tags t ON ft.tag_sn = t.tag_sn
WHERE ft.feed_sn = #{feedSn}
