mapper.select:
/* 피드 상세 페이지에서 다음 피드 목록 조회 */ SELECT f.feed_sn, f.wrtr_user_no, f.feed_cn, f.crt_dt, u.user_no, u.user_ncnm, up.prfl_img, fi.feed_img_sn, fi.img_file_path_nm, fi.feed_img_sort_sn, (SELECT COUNT(*) FROM feed_interactions fi2 WHERE fi2.feed_sn = f.feed_sn AND fi2.intract_type_cd = 'LIKE') AS like_count FROM ( SELECT * FROM feeds WHERE crt_dt < STR_TO_DATE(#{currentFeedCrtDt}, '%Y-%m-%d %H:%i:%s') AND del_dt IS NULL
AND wrtr_user_no = #{userNo}
ORDER BY crt_dt DESC LIMIT #{limit} ) f LEFT JOIN users u ON f.wrtr_user_no = u.user_no LEFT JOIN user_profiles up ON u.user_no = up.user_no LEFT JOIN feed_images fi ON f.feed_sn = fi.feed_sn ORDER BY f.crt_dt DESC, fi.feed_img_sort_sn ASC

/* 피드 목록 조회 (통합) */ SELECT f.feed_sn, f.wrtr_user_no, f.feed_cn, f.crt_dt, u.user_no, u.user_ncnm, fi.img_file_path_nm, ( SELECT COUNT(fi2.feed_intract_sn) FROM feed_interactions fi2 WHERE fi2.feed_sn = f.feed_sn AND fi2.intract_type_cd = 'LIKE' ) AS like_count FROM feeds f LEFT JOIN users u ON f.wrtr_user_no = u.user_no LEFT JOIN feed_images fi ON f.feed_sn = fi.feed_sn AND fi.feed_img_sort_sn = 0 WHERE f.del_dt IS NULL
AND f.wrtr_user_no = #{userNo}
ORDER BY f.crt_dt DESC LIMIT #{limit} OFFSET #{offset}
