mapper.select:
/* 게시판 글 목록 조회 */ SELECT p.pst_sn, p.bbs_clsf_cd, p.pst_ttl, p.pst_cn, p.view_cnt, p.ntc_pst_yn, p.crt_dt, p.mdfcn_dt, p.del_dt, b.bbs_nm, u.user_ncnm, (SELECT COUNT(pc.pst_cmnt_sn) FROM post_comments pc WHERE pc.pst_sn = p.pst_sn And pc.del_dt IS null) AS cmnt_cnt, (SELECT COUNT(pi.pst_intract_sn) FROM post_interactions pi WHERE pi.pst_sn = p.pst_sn AND pi.intract_type_cd = 'LIKE' AND pi.rtrcn_dt IS NULL) AS inter_cnt FROM posts p JOIN boards b ON p.bbs_clsf_cd = b.bbs_clsf_cd LEFT JOIN users u ON p.wrtr_user_no = u.user_no WHERE p.del_dt IS NULL
AND p.bbs_clsf_cd = #{bbsClsfCd}
ORDER BY p.crt_dt DESC LIMIT #{limit} OFFSET #{offset};

/* 특정 게시판 게시글 갯수 조회 */
SELECT
	COUNT(p.pst_sn)
FROM
	posts p
WHERE
	p.del_dt is null
	
		AND p.bbs_clsf_cd = #{bbsClsfCd}
	;
