mapper.select:
/* 게시판 글 목록 조회 */ SELECT p.pst_sn AS pstSn, p.bbs_clsf_cd AS bbsClsfCd, p.wrtr_user_no AS wrtrUserNo, p.pst_ttl AS pstTtl, p.pst_cn AS pstCn, p.inq_cnt AS inqCnt, p.ntc_pst_yn AS ntcPstYn, p.crt_dt AS crtDt, p.mdfcn_dt AS mdfcnDt, p.del_dt AS delDt, b.bbs_nm AS bbsNm, u.user_ncnm AS userNcnm, (SELECT COUNT(pc.pst_cmnt_sn) FROM post_comments pc WHERE pc.pst_sn = p.pst_sn AND pc.del_dt IS NULL) AS commentCount, (SELECT COUNT(pi.pst_intract_sn) FROM post_interactions pi WHERE pi.pst_sn = p.pst_sn AND pi.intract_type_cd = 'LIKE' AND pi.rtrcn_dt IS NULL) AS likesCount FROM posts p JOIN boards b ON p.bbs_clsf_cd = b.bbs_clsf_cd LEFT JOIN users u ON p.wrtr_user_no = u.user_no WHERE p.del_dt IS NULL
AND p.bbs_clsf_cd = #{bbsClsfCd}
ORDER BY p.crt_dt DESC LIMIT #{limit} OFFSET #{offset}
