mapper.insert:
INSERT INTO payments (
    stlm_id,            ordr_no,        user_no,
    stlm_amt,           stlm_stts_cd,   pg_dlng_id,
    pg_co_info,         stlm_dmnd_dt
) VALUES (
    #{stlmId},          #{ordrNo},          #{userNo},
    #{stlmAmt},         #{stlmSttsCd},      #{pgDlngId},
    #{pgCoInfo},        #{stlmDmndDt}
)

mapper.update:
UPDATE 
	store_settlements
SET 
	tot_sel_amt = IFNULL(tot_sel_amt, 0) + #{salesAmount}
WHERE 
	store_id = #{storeId}

mapper.delete:
DELETE FROM cart_items
WHERE
    user_no = #{userNo}
    AND gds_no IN
    
        #{item.gdsNo}
