gds_nm = #{gdsNm}, gds_expln = #{gdsExpln}, ctgry_no = #{ctgryNo}, bas_prc = #{basPrc}, dscnt_rt = #{dscntRt}, last_sel_prc = #{lastSelPrc}, min_purchase_qty = #{minPurchaseQty}, max_purchase_qty = #{maxPurchaseQty}, mdfr_no = #{mdfrNo}, mdfcn_dt = CURRENT_TIMESTAMP
WHERE gds_no = #{gdsNo}
